<template>
  <div id="app">
    <h1>vue-drag-verify2</h1>
    <h3>基于vue-drag-verify二次开发的vue组件</h3>
    <drag-verify
      class="verify"
      ref="dragVerify"
      :isPassing.sync="isPassing"
      handlerIcon="fa fa-angle-double-right"
      successIcon="fa fa-check-circle-o"
    >
    </drag-verify>
    <button
      style="margin-top:10px"
      @click="handleReset"
    >reset</button>

    <h2><a href="https://yimijianfang.github.io/vue-drag-verify/#/">doc&demo</a></h2>
  </div>
</template>

<script>
import dragVerify from "./packages/vue-drag-verify";
export default {
  name: "app",
  components: {
    dragVerify
  },
  data() {
    return {
      isPassing: false
    };
  },
  methods: {
    handleReset() {
      this.isPassing = false;
      this.$refs.dragVerify.reset();
    }
  }
};
</script>

